mapper.select:
SELECT houseinfo.dong, houseinfo.AptName, housedeal.dealAmount, housedeal.dealDay, housedeal.dealMonth, housedeal.dealYear, housedeal.area, houseinfo.lat, houseinfo.lng FROM houseinfo, housedeal WHERE houseinfo.no = housedeal.houseinfoid
and houseinfo.AptName like concat('%', #{word}, '%')
and houseinfo.dong = #{word}
